import logo from './logo.svg';
import scrollDown from './scrollDown.png';
import './App.css';
import React, {useState} from 'react';

function App() {
  const [question, setQuestion] = useState(null);
  const [context, setContext] = useState(null);

  function getQuestion(val) {
    setQuestion(val.target.value);
    console.log(val.target.value);
  }

  function getContext(val) {
    setContext(val = context);
  }


  return (
    <div id="app" className="App">
      <header className="App-header">
        <img className="App-logo" src={logo} alt="logo" />
        <h1>Selamat Datang!</h1>
        <img className="button-scroll" src={scrollDown} alt="logo" />
      </header>

      <body className="App-body">
      <h1>Cari disini</h1>
      <div className="box-question">
        <p>Pertanyaan</p>
        <input id="question" className="box-question-input" type="text" placeholder="Type here..." onChange={getQuestion} />
      </div>
      <div className="box-context">
        <p>Konteks</p>
        <textarea id="context" className="box-context-input" type="text" placeholder="Type here..." onChange={getContext} />
      </div>
          <button id="submit" className="button-submit" >Cari Jawaban</button>
      </body>

      <footer className="App-footer">

      </footer>
    </div>
  );
}
export default App;
