import logo from './logo.svg';
import './App.css';
import React, {useState} from 'react';

function App() {
  const [question, setQuestion] = useState(null);
  const [context, setContext] = useState(null);
  const [submit, setSubmit] = useState(false);

  function getQuestion(val) {
    setQuestion(val.target.value);
  }

  function getContext() {
    setContext();
  }

  React.useEffect(
    function () {
      console.log(document.getElementById('context'));
    },
    [question]
  )


  return (
    <div className="App">
      <header className="App-header">
        <img className="App-logo" src={logo} alt="logo" />
        <h1 className="heading-1">React JS Ui</h1>
      </header>

      <body className="App-body">
        <div className="">
          <input id="question" className="box-question" type="text" placeholder="Type here..." onChange={getQuestion}/>
          <button className="button-submit" type="submit">Find</button>
        </div>
        <div className="">
          <input id="context" type="text" className="box-context" placeholder="Type here..."/>
        </div>
      </body>

      <footer className="App-footer">

      </footer>
    </div>
  );
}
export default App;
