import './App.css';

function App() {
  function post() {
    //do your code here..
    alert('Hello');
  }

  function Mouse() {
    return (
      <div className='mouse_scroll'>
        <div className='mouse'>
          <div className='wheel'></div>
        </div>
        <div>
          <span className='m_scroll_arrows one'></span>
          <span className='m_scroll_arrows two'></span>
          <span className='m_scroll_arrows three'></span>
        </div>
      </div>
    );
  }

  function reveal() {
    var reveals = document.querySelectorAll(".reveal");

    for (var i = 0; i < reveals.length; i++) {
      var windowHeight = window.innerHeight;
      var elementTop = reveals[i].getBoundingClientRect().top;
      var elementVisible = 150;

      if (elementTop < windowHeight - elementVisible) {
        reveals[i].classList.add("active");
      } else {
        reveals[i].classList.remove("active");
      }
    }
  }

  window.addEventListener("scroll", reveal);

  return (
    <div className='App'>
      <div className='container'>
        <h1 className='heading-1'>Selamat Datang!<br/></h1>
        <p className='desc'>
          Website ini merupakan Question & Answering website yang kami kembangkan 
          untuk memenuhi dalam penilaian UAS pada matakulah Pemrosesan Bahasa Alami. 

        </p>
        <Mouse />
      </div>

      <div className='container reveal'>
        <div className='content'>
          <p className='desc'>
          Masukan apa yang ingin kamu tanyakan pada kolom pertanyaan. 
          Berikan juga konteks untuk pertanyaan yang kamu buat, agar model kami dapat memprosesnya untuk mencari jawaban dari pertanyaanmu.
          </p>
          <div className='row'>
            <div className='col-25'>
              <label>Question</label>
            </div>
            <div className='col-75'>
              <input type='text' id="question-box" name="question-box" placeholder='Type here...'/>
            </div>
          </div>
          <div className='row'>
            <div className='col-25'>
              <label>Context</label>
            </div>
            <div className='col-75'>
              <textarea id='context-box' name='context-box' placeholder='Write something...'/>
            </div>
          </div>
          <div className='row'>
            <input type='submit' value='Submit' onClick={post}/>
          </div>
          <div className='row'>
            <div className='col-25'>
              <label>Answer</label>
            </div>
            <div className='col-75'>
              <text className='answer'>
                Your answer will appear here
              </text>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
export default App;
