import './App.css';

function App() {

  function Mouse() {
    return (
      <div class="mouse_scroll">
        <div class="mouse">
          <div class="wheel"></div>
        </div>
        <div>
          <span class="m_scroll_arrows one"></span>
          <span class="m_scroll_arrows doi"></span>
          <span class="m_scroll_arrows trei"></span>
        </div>
    </div>
    );

  }

  function reveal() {
    var reveals = document.querySelectorAll(".reveal");

    for (var i = 0; i < reveals.length; i++) {
      var windowHeight = window.innerHeight;
      var elementTop = reveals[i].getBoundingClientRect().top;
      var elementVisible = 150;

      if (elementTop < windowHeight - elementVisible) {
        reveals[i].classList.add("active");
      } else {
        reveals[i].classList.remove("active");
      }
    }
  }

  window.addEventListener("scroll", reveal);

  return (
    <div id="app" className="App">
      <header className="App-header">
        <h1 className="heading-1">Selamat Datang!</h1>
        <p>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. 
        Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. 
        Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. 
        Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </p>
        <Mouse />
      </header>

      <body className="App-body reveal">
        <h1>Cari disini</h1>
        <div className="box-question">
          <p>Pertanyaan</p>
          <input id="question" className="box-question-input" type="text" placeholder="Type here..." />
        </div>
        <div className="box-context">
          <p>Konteks</p>
          <textarea id="context" className="box-context-input" type="text" placeholder="Type here..."/>
        </div>
            <button id="submit" className="button-submit" >Cari Jawaban</button>
      </body>

      <footer className="App-footer">

      </footer>
    </div>
  );
}
export default App;
