import './App.css';
import { useState } from 'react';

function App() {

  const [question, setQuestion] = useState(null);
  const [context, setContext] = useState(null);
  const [post, setPost] = useState(false);
  const [answer, setAnswer] = useState(null);

  function DescJudul(){
    return (
      <p className='desc'>
        Deskripsi judul ditulis disini...
      </p>
    );
  }

  function DescContent(){
    return (
      <p className='desc'>
        Deskripsi content ditulis disini...
      </p>
    );
  }

  function Print() {
    return(
      <div className='content '>
        <div className='col-25'>
          <label>Answer</label>
        </div>
        <div className='col-75'>
        <p>{answer}</p>
        </div>
      </div>
    );
  }

  function getQuestion () {
    setQuestion(document.getElementById("question-box"));
  }

  function getContext (context) {
    setContext(document.getElementById(context));
  }

  function getAnswer () {
    setAnswer('Answer');
  }

  function getPost() {
    getAnswer();
    console.warn(answer);
    console.warn(question);
    console.warn(context);
    alert('Loading..')
    setPost (true);
  }


  function Mouse() {
    return (
      <div className='mouse_scroll'>
        <div className='mouse'>
          <div className='wheel'/>
        </div>
        <div>
          <span className='m_scroll_arrows one'></span>
          <span className='m_scroll_arrows two'></span>
          <span className='m_scroll_arrows three'></span>
        </div>
      </div>
    );
  }

  function reveal() {
    var reveals = document.querySelectorAll(".reveal");

    for (var i = 0; i < reveals.length; i++) {
      var windowHeight = window.innerHeight;
      var elementTop = reveals[i].getBoundingClientRect().top;
      var elementVisible = 150;

      if (elementTop < windowHeight - elementVisible) {
        reveals[i].classList.add("active");
      } else {
        reveals[i].classList.remove("active");
      }
    }
  }

  window.addEventListener("scroll", reveal);

  return (
    <div className='App'>
      <div className='container'>
        <h1 className='heading-1'>Selamat Datang!<br/></h1>
        <DescContent/>
        <Mouse />
      </div>

      <div className='container reveal'>
        <div className='content'>
          <h1 className='heading-1'>Ayo tanyakan!</h1>
          <DescJudul/>  
          <div className='row'>
            <div className='col-25'>
              <label>Question</label>
            </div>
            <div className='col-75'>
              <input type='text' id="question-box" name="question-box" placeholder='Type here...' onSubmit={getQuestion}/>
            </div>
          </div>
          <div className='row'>
            <div className='col-25'>
              <label>Context</label>
            </div>
            <div className='col-75'>
              <textarea id='context-box' name='context-box' placeholder='Write something...' onSubmit={getContext}/>
            </div>
          </div>
          <div className='row'>
            <input type='submit' value='Submit' onClick={getPost}/>
          </div>
        </div>
        {
          post?
          <Print/>
          :null
        }
      </div>
    </div>
  );
}
export default App;
